const CreateMap = () => {
    return ( 
        <div>
            <Navbar/>
            <h1>CreateMap</h1>
            <div className="upload">
                <form onSubmit={uplaodFile}>
                    <div>
                        <div
                            className="dragUpload"
                            onDragOver={handleDrageOver}
                            onDrop={handleDrop}
                        >
                            <p>Drag to upload</p>
                            { !csvFile && <input type="file" name="file" id="files" onChange={(e) => { setCsvFile(e.target.files[0]);setFileName(e.target.files[0].name)}}/> }
                            { csvFile && <p>{fileName}</p> }
                            <br />
                        </div>
                        <input type="submit" value="Upload" className="btnU" />
                    </div>
                </form>
            </div>
        </div>
     );
}
 
export default CreateMap;
