import React from "react";
import { useState } from "react";

const UseMap = () => {

    const [csvFile, setCsvFile] = useState()

    const formData = new FormData();


    async function uplaodFile(event) {
        event.preventDefault()

        // console.log(document.querySelector("#files"))
        // setCsvFile(event.target.files);
        console.log(csvFile)
        const formData = new FormData();
        formData.append('name', event.target.name   );
        formData.append('file', csvFile);

        // console

        const response = await fetch('http://localhost:1337/api/uploadCsv', {
            method: 'POST',
            headers: {
                'Content-Type': 'text/csv'
            },
            body: csvFile
        })
        .then(res => console.log(res.json()))
        .catch(err => console.log(err));
    }

    return (
        <div>
            <h1>UseMap</h1>
            <div>
                <form onSubmit={uplaodFile}>
                    <div>
                        <div className="dragUpload">
                            <p>Drag to upload</p>
                        </div>
                        <input type="file" name="file" id="files" onChange={(e) =>{setCsvFile(e.target.value)}}/>
                        <br />
                        <input type="submit" value="Upload" className="btnU" />
                    </div>
                </form>

            </div>
        </div>
    );
}

export default UseMap;
