import React from "react"
import Navbar from "./Navbar";
import { useState } from "react";
import {useLocation, useNavigate} from "react-router-dom";  
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

const Dashboard = () => {
    const [mappings, setMappings] = useState([]);

    const onDragStart = (event, fieldName) => {
      event.dataTransfer.setData('fieldName', fieldName);
    };
  
    const onDrop = (event, columnName) => {
      const fieldName = event.dataTransfer.getData('fieldName');
      const mapping = { fieldName, columnName };
      setMappings([...mappings, mapping]);
      console.log(mappings)
    };
  
    const Column = ({ fields, columnName }) => {
      return (
        <div
          onDrop={(event) => onDrop(event, columnName)}
          onDragOver={(event) => event.preventDefault()}
          style={{ border: '1px solid black', padding: '10px' }}
        >
          {fields.map((field) => (
            <div
              key={field}
              draggable
              onDragStart={(event) => onDragStart(event, field)}
              style={{ margin: '5px',border: '1px solid black' }}
            >
              {field}
            </div>
          ))}
        </div>
      );
    };
  
