import React from "react";
import { useState } from "react";

const UseMap = () => {

    const [csvFile, setCsvFile] = useState('')

    const formData = new FormData();

    const handleDrageOver = (event) => {
        event.preventDefault();
        
    }

    const handleDrop = (event) => {
        event.preventDefault();
        setCsvFile(event.dataTransfer.files[0])
        console.log(event.dataTransfer.files[0])
    }

    async function setVar(files){
        return files.files[0]
    }

    async function uplaodFile(event) {
        event.preventDefault()

        const files = document.getElementById("files");

        // setCsvFile(files.files[0])
        const csv = await setVar(files)

        // console.log(document.querySelector("#files"))
        // setCsvFile(event.target.files);
        // setCsvFile(document.querySelector("#files"));
        // console.log(csvFile)
        const formData = new FormData();
        formData.append('name', 'csv_File');
        // formData.append('file', files.files[0]);
        formData.append('file', csv);

        console.log(...formData)

        const response = await fetch('http://localhost:1337/api/uploadcsv', {
            method: 'POST',
            // headers: {
            //     'Content-Type': 'text/csv'
            // },
            body: formData
        })
        // .then(res => console.log(res.json()))
        // .catch(err => console.log(err));

        const data = await response.json()

        console.log(data)

        if (data.status === 'ok') {
            alert('File Uploaded')
        } else {
            alert("File couldn't be uploaded")
        }

    }

    return (
        <div>
            <h1>UseMap</h1>
            <div>
                <form onSubmit={uplaodFile}>
                    <div>
                        <div 
                            className="dragUpload"
                            onDragOver={handleDrageOver}
                            onDrop={handleDrop}
                        >
                            <p>Drag to upload</p>
                            <input type="file" name="file" id="files" />
                            <br />
                        </div>
                        <input type="submit" value="Upload" className="btnU" />
                    </div>
                </form>

            </div>
        </div>
    );
}

export default UseMap;
